// src/mongoConfig.js
import * as Realm from "realm-web";

const app = new Realm.App({ id: "6678567ecca8491bfe7eeeb8" }); // Thay "your-app-id" bằng App ID của bạn từ MongoDB Realm

export const getMongoClient = async () => {
    if (!app.currentUser) {
        await app.logIn(Realm.Credentials.anonymous());
    }
    return app.currentUser.mongoClient("mongodb-atlas");
};

export const getDatabase = async () => {
    const client = await getMongoClient();
    return client.db("MeoMePhim"); // Thay "your-database-name" bằng tên cơ sở dữ liệu của bạn
};
