// src/components/ManageMovies.js
import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Box, Table, TableContainer, TableHead, TableRow, TableCell, TableBody, Paper, IconButton, TablePagination } from '@mui/material';
import { Delete, Visibility, Refresh } from '@mui/icons-material';
import { getDatabase } from '../mongoConfig';

const ManageMovies = () => {
  const [urlApi, setUrlApi] = useState('');
  const [movies, setMovies] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(20);

  useEffect(() => {
    const fetchMovies = async () => {
      const db = await getDatabase();
      const allMovies = await db.collection("movies").find(); // Assuming "movies" is the collection name
      setMovies(allMovies);
    };
    fetchMovies();
  }, []);

  const handleUrlInputChange = (event) => {
    setUrlApi(event.target.value);
  };

  const handleSearchInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const newMovie = {
      movie_id: movies.length + 1,
      movie_api_url: urlApi,
      movie_name: 'Tên phim',
      movie_origin_name: 'Tên gốc phim',
      movie_country: 'Quốc gia',
      movie_category: 'Thể loại'
    };

    const db = await getDatabase();
    await db.collection("movies").insertOne(newMovie);
    setMovies([...movies, newMovie]);
    setUrlApi('');
  };

  const handleDelete = async (id) => {
    const db = await getDatabase();
    await db.collection("movies").deleteOne({ movie_id: id });
    setMovies(movies.filter(movie => movie.movie_id !== id));
  };

  const filteredMovies = movies.filter((movie) =>
    movie.movie_name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  return (
    <Box sx={{ maxWidth: 1000, mx: 'auto', mt: 4, p: 3, bgcolor: 'background.paper', boxShadow: 1, borderRadius: 4 }}>
      <Typography variant="h4" gutterBottom>
        Quản lý phim
      </Typography>
      <Typography variant="body1" gutterBottom>
        Đây là trang quản lý phim.
      </Typography>

      <form onSubmit={handleSubmit}>
        <TextField
          label="URL API phim"
          fullWidth
          value={urlApi}
          onChange={handleUrlInputChange}
          variant="outlined"
          margin="normal"
          placeholder="Nhập URL API phim..."
        />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          disableElevation
          style={{ marginTop: 16 }}
        >
          Thêm phim
        </Button>
      </form>

      <TextField
        label="Tìm kiếm phim"
        fullWidth
        value={searchTerm}
        onChange={handleSearchInputChange}
        variant="outlined"
        margin="normal"
        placeholder="Tìm kiếm phim..."
      />

      <Box sx={{ mt: 3 }}>
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>STT</TableCell>
                <TableCell>Movie ID</TableCell>
                <TableCell>Movie API URL</TableCell>
                <TableCell>Movie Name</TableCell>
                <TableCell>Movie Origin Name</TableCell>
                <TableCell>Movie Country</TableCell>
                <TableCell>Movie Category</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {filteredMovies.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((movie, index) => (
                <TableRow key={movie.movie_id}>
                  <TableCell>{index + 1}</TableCell>
                  <TableCell>{movie.movie_id}</TableCell>
                  <TableCell>{movie.movie_api_url}</TableCell>
                  <TableCell>{movie.movie_name}</TableCell>
                  <TableCell>{movie.movie_origin_name}</TableCell>
                  <TableCell>{movie.movie_country}</TableCell>
                  <TableCell>{movie.movie_category}</TableCell>
                  <TableCell>
                    <IconButton color="primary">
                      <Visibility />
                    </IconButton>
                    <IconButton color="secondary">
                      <Refresh />
                    </IconButton>
                    <IconButton color="error" onClick={() => handleDelete(movie.movie_id)}>
                      <Delete />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
        <TablePagination
          component="div"
          count={filteredMovies.length}
          page={page}
          onPageChange={handleChangePage}
          rowsPerPage={rowsPerPage}
          onRowsPerPageChange={handleChangeRowsPerPage}
          rowsPerPageOptions={[10, 20, 50]}
          labelRowsPerPage="Số hàng mỗi trang"
        />
      </Box>
    </Box>
  );
};

export default ManageMovies;
