import React, { useState } from 'react';
import { TextField, Button, Typography, Box, Table, TableContainer, TableHead, TableRow, TableCell, TableBody, Paper } from '@mui/material';

const ManageMovies = () => {
  const [urlApi, setUrlApi] = useState('');
  const [movies, setMovies] = useState([]);

  const handleUrlInputChange = (event) => {
    setUrlApi(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Thực hiện xử lý khi người dùng submit form, ví dụ: thêm phim vào danh sách movies
    const newMovie = {
      movie_id: movies.length + 1, // Tạo movie_id dựa trên số lượng phim hiện tại
      movie_api_url: urlApi,
      movie_name: 'Tên phim', // Thay bằng giá trị thực nếu có
      movie_origin_name: 'Tên gốc phim', // Thay bằng giá trị thực nếu có
      movie_country: 'Quốc gia', // Thay bằng giá trị thực nếu có
      movie_category: 'Thể loại' // Thay bằng giá trị thực nếu có
    };

    setMovies([...movies, newMovie]);
    setUrlApi(''); // Reset ô input sau khi thêm phim thành công
  };

  return (
    <Box sx={{ maxWidth: 800, mx: 'auto', mt: 4, p: 3, bgcolor: 'background.paper', boxShadow: 1, borderRadius: 4 }}>
      <Typography variant="h4" gutterBottom>
        Quản lý phim
      </Typography>
      <Typography variant="body1" gutterBottom>
        Đây là trang quản lý phim.
      </Typography>

      <form onSubmit={handleSubmit}>
        <TextField
          label="URL API phim"
          fullWidth
          value={urlApi}
          onChange={handleUrlInputChange}
          variant="outlined"
          margin="normal"
          placeholder="Nhập URL API phim..."
        />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          disableElevation
          style={{ marginTop: 16 }}
        >
          Thêm phim
        </Button>
      </form>

      <Box sx={{ mt: 3 }}>
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Movie ID</TableCell>
                <TableCell>Movie API URL</TableCell>
                <TableCell>Movie Name</TableCell>
                <TableCell>Movie Origin Name</TableCell>
                <TableCell>Movie Country</TableCell>
                <TableCell>Movie Category</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {movies.map((movie) => (
                <TableRow key={movie.movie_id}>
                  <TableCell>{movie.movie_id}</TableCell>
                  <TableCell>{movie.movie_api_url}</TableCell>
                  <TableCell>{movie.movie_name}</TableCell>
                  <TableCell>{movie.movie_origin_name}</TableCell>
                  <TableCell>{movie.movie_country}</TableCell>
                  <TableCell>{movie.movie_category}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Box>
    </Box>
  );
};

export default ManageMovies;
