import * as Realm from "realm-web";

const app = new Realm.App({ id: "meomephim-zhfuvpz" }); // Thay "your-app-id" bằng App ID của bạn từ MongoDB Realm

export const loginWithEmailAndPassword = async (email, password) => {
    try {
        const credentials = Realm.Credentials.emailPassword(email, password);
        const user = await app.logIn(credentials);
        console.log("Logged in:", user.id);
        return user;
    } catch (error) {
        console.error("Login failed:", error);
        throw error;
    }
};

// Sau khi đăng nhập thành công, bạn có thể lấy currentUser và sử dụng Mongo Client
export const getMongoClient = async () => {
    if (!app.currentUser) {
        throw new Error("User not authenticated");
    }
    return app.currentUser.mongoClient("mongodb-atlas");
};

export const getDatabase = async () => {
    const client = await getMongoClient();
    return client.db("your-database-name"); // Thay "your-database-name" bằng tên cơ sở dữ liệu của bạn
};
