// src/context/AppContext.js
import React, { createContext, useState, useEffect } from 'react';
import { getDatabase } from '../mongoConfig';

export const AppContext = createContext();

export const AppProvider = ({ children }) => {
  const [visitorCount, setVisitorCount] = useState(0);
  const [movieCount, setMovieCount] = useState(0);

  useEffect(() => {
    const fetchVisitorCount = async () => {
      const db = await getDatabase();
      const visitorCollection = db.collection('visitors');
      const visitorData = await visitorCollection.findOne({ name: 'visitorCount' });
      setVisitorCount(visitorData ? visitorData.count : 0);
    };

    const fetchMovieCount = async () => {
      const db = await getDatabase();
      const movieCollection = db.collection('movies');
      const movieData = await movieCollection.countDocuments();
      setMovieCount(movieData);
    };

    fetchVisitorCount();
    fetchMovieCount();
  }, []);

  return (
    <AppContext.Provider value={{ visitorCount, movieCount, setVisitorCount }}>
      {children}
    </AppContext.Provider>
  );
};
